import React from 'react';
import './cart.scss'

const cart = (props)=>{

    let items =<h3>Cart is Empty</h3>;
    if(props.cart.length>0)
    {
      items =  props.cart.map(i=>(
        <div className="product">
            <div className='product-image'>
                <img src={i.imageUrl}/>
            </div>
            <div className="product-details">
              <div className="product-title">{i.title}</div>
              <p className="product-description">{i.description}</p>
            </div>
            <div className="product-price">{i.price}</div>
            <div className="product-quantity">
              <input type="number" value="2" min="1"/>
            </div>
            <div className="product-removal">
              <button className="remove-product">
                Remove
              </button>
              
            </div>
            <div className="product-line-price">65</div>
          </div>
            ))
    }



    return(
        <div className="shopping-cart">
        <h1>Shopping Cart</h1> 
        <div className="column-labels">
        <label className="product-image">Image</label>
        <label className="product-details">Product</label>
        <label className="product-price">Price</label>
        <label className="product-quantity">Quantity</label>
        <label className="product-removal">Remove</label>
        <label className="product-line-price">Total</label>
      </div>

        {items}

     <div className="totals">
    <div className="totals-item">
      <label>Subtotal</label>
      <div className="totals-value" id="cart-subtotal">71.97</div>
    </div>
    <div className="totals-item">
      <label>Tax (5%)</label>
      <div className="totals-value" id="cart-tax">3.60</div>
    </div>
    <div className="totals-item">
      <label>Shipping</label>
      <div className="totals-value" id="cart-shipping">15.00</div>
    </div>
    <div className="totals-item totals-item-total">
      <label>Grand Total</label>
      <div className="totals-value" id="cart-total">90.57</div>
    </div>
  </div>
      
      <button className="checkout">Checkout</button>

      </div>
    )
}




export default cart;

