import React from 'react';
import './cart.scss'
import {Link} from 'react-router-dom';

const cart = (props)=>{

console.log(props.cart)

    let caritems =<h3>Cart is Empty</h3>;
    if(props.cart.items.length>0)
    {
      items =  props.cart.items.map(i=>
        {
        let  totalPrice= i.product.price;


        const changeHandler=(event)=>{
          totalPrice = event.target.value * i.product.price;
        console.log(totalPrice)
          }
          console.log(totalPrice)
          let cartItems = (       <div className="product">
          <div className='product-image'>
              <img src={i.product.imageUrl}/>
          </div>
          <div className="product-details">
            <div className="product-title">{i.product.title}</div>
            <p className="product-description">{i.product.description}</p>
          </div>
          <div className="product-price">{i.product.price}</div>
          <div className="product-quantity">
            <input defaultValue='1' type="number" onChange={(event)=>changeHandler(event)}  min="1"/>
          </div>
          <div className="product-removal">
            <button onClick={()=>props.remove(i.product._id, i.product.price)} className="remove-product">
              Remove
            </button>
            
          </div>
          <div className="product-line-price">{totalPrice}</div>
        </div>
        )
        return(
          {cartItems}
   )})
    }



    return(
        <div className="shopping-cart">
        <h1>Shopping Cart</h1> 
        <div className="column-labels">
        <label className="product-image">Image</label>
        <label className="product-details">Product</label>
        <label className="product-price">Price</label>
        <label className="product-quantity">Quantity</label>
        <label className="product-removal">Remove</label>
        <label className="product-line-price">Total</label>
      </div>

        {items}

     <div className="totals">
    <div className="totals-item">
      <label>Subtotal</label>
      <div className="totals-value" id="cart-subtotal">{props.cart.totalPrice? props.cart.totalPrice : 0}</div>
    </div>
   
    <div className="totals-item">
      <label>Shipping</label>
      <div className="totals-value" id="cart-shipping">15.00</div>
    </div>
    <div className="totals-item totals-item-total">
      <label>Grand Total</label>
      <div className="totals-value" id="cart-total">{+props.cart.totalPrice + 15.00 }</div>
    </div>
  </div>
      
      <Link to='/Checkout' >
         <button  className="checkout">
         Checkout
         </button>
         </Link>

      </div>
    )
}




export default cart;

