import React, {Component} from 'react';
import './productView.css'
class ProductView extends Component{

state={
    product: null
}


componentDidMount() {
    const productId = this.props.match.params.postId;

       
        fetch('http://localhost:8080/get-product/' + prodId,
        { method: "GET",
          
          }).then(res=>{
            if(res.status!==200){
              throw new Error(' could not fetch product')
            }
            console.log(res)
            return res.json()
          }).then(resData=>{
            console.log(resData)
            const product= resData.product
            this.setState=({
              product:product
            })
          }).catch(err=>{
            console.log(err)
          })
      
      
}

     
   
    render(){
        console.log(this.state)
       const product = this.state.product;
       if(!product)
       {
           return null
       }
return (<main className='centered'>
            <h1>{product.title}</h1>
            <div >
                <img class="image" src={product.imageUrl} alt={product.title} />
            </div>
            <h2>{product.price}</h2>
            <p>{product.description}</p>
            
            </main> )

    }
    
    
}

export default ProductView;
