import React,{Component} from 'react';
import './orders.css'
class Order extends Component {
  state = {
      orders: null
  }

 
  componentDidMount(){
    console.log(this.props)
      fetch('http://localhost:8080/get-orders',{
          
          headers:{
            
                Authorization: 'Bearer ' + this.props.token,
            }
      }).then(res=>{
          if(res.status!==200 && res.status!==201){
              const error = new Error('Could not fetch Orders');
              throw error
          }
          return res.json()
      }).then(resData=>{
          console.log(resData)
          this.setState({orders: resData.orders})
      }).catch(err=>{
          console.log(err)
      })
  }


//  <ul class="orders">
//                     <% orders.forEach(order => { %>
//                         <li class="orders__item">
//                             <h1>Order - # <%= order._id %></h1>
//                             <ul class="orders__products">
//                                 <% order.products.forEach(p => { %>
//                                     <li class="orders__products-item"><%= p.product.title %> (<%= p.quantity %>)</li>
//                                 <% }); %>
//                             </ul>
//                         </li>
//                     <% }); %>
//                 </ul>

  
    render(){
        let orders;
        if(!this.state.orders)
        {
           return orders = <h1>No orders</h1>
        }
      
        orders = this.state.orders.map(order=>{
            console.log( order.orderItems.items)
            const items = order.orderItems.items.map(item=>{
                
                return(<li>
                {"Product: " + item.product.title + " " + item.quantity}
                </li>)
                
                  
            })
            return( 
                <li className='orders__item'>
                    <h1>
                        Order - {order._id}
                       
                    </h1>
                    <h1> Name: {order.name}</h1>
                    <h1> Adress: {order.address}</h1>
                    <h1></h1>
                    <ul className="orders__products-item">
                    
                            {items}
                    </ul>
                </li>)
            })
        return <ul className="orders">
            {orders}
        </ul>
    }

}

export default Order
