import Header from '../../components/Header/Header';
import React, {Component} from 'react';
import Shop from '../../components/shop/shop';
import Auxiliary from '../../hoc/Auxiliary/Auxiliary';
import SideBar from '../../components/SideBar/sideBar';
import { Route, Switch, Redirect, withRouter } from 'react-router-dom';
import AddProductForm from '../../components/Add-Product From/addProductForm'
import Signup from '../../components/Auth/signup/signup';
import Login from '../../components/Auth/login/login';
import ProductView from '../../components/Article/Product-View/product-view';
import Cart from '../../components/cart/cart';
import Checkout from '../../components/checkout/checkout';

class Body extends Component {

  state={  
    selectedProduct: null,
    loggedInAdmin : null,
    isAuth : false,
    products: null,
    cart: {
      items:[],
     totalPrice: null
    },
    token : null

}
componentDidMount(){
  fetch('http://localhost:8080/get-products')
  .then(res=>{
      
      return res.json();
   
  }).then(resData=>{
      console.log(resData)
      this.setState({
          products: resData.products
      })
  })
  .catch(err=>{
      console.log(err)
  })
}


signupHandler=(adminData)=>{
  console.log(adminData)
  fetch('http://localhost:8080/signup/',{
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({

      email: adminData.email,
      password: adminData.password,
      name: adminData.name

    })
  }).then(res=>{
    if(res.status!==200 && res.status!==201)
    {
      throw new Error('create admin failed')
    
    }
    return res.json();
}).then(resData=>{
  console.log(resData)
  
}).catch(err=>{
  console.log(err)
})
this.props.history.replace('/home')
}

loginHandler=(loginData)=>{

  console.log(loginData)
  fetch('http://localhost:8080/login',{
    method: 'POST',
    headers:{
      'Content-Type' : 'application/json'
    },
    body: JSON.stringify({
      email: loginData.email,
      password: loginData.password
    })
  }).then(res=>{
    if(res.status!==200 && res.status !==201){
      throw new Error('Login failed')
    }
    return res.json()
  }).then(resData=>{
   console.log(resData)
    this.setState({
      isAuth: true,
      token: resData.token

    })
    localStorage.setItem('token', resData.token);
    localStorage.setItem('adminId', resData.adminId)
    const remainingMilliseconds = 60 * 60 * 1000;
    const expiryDate = new Date(
      new Date().getTime() + remainingMilliseconds
    );
    localStorage.setItem('expiryDate', expiryDate.toISOString());
    this.setAutoLogout(remainingMilliseconds);

  })
 
 

}
setAutoLogout = milliseconds => {
  setTimeout(() => {
    this.logoutHandler();
  }, milliseconds);
};
addProductHandler=(prodData)=>{

fetch('http://localhost:8080/add-product/',{
       method: 'POST',
       headers: {
        'Content-Type': 'application/json',
          },
       body: JSON.stringify({
        title: prodData.title,
        price: prodData.price,
        description: prodData.description,
       imageUrl : '',
       })
  }).then(res=>{
    if(res.status!==200 && res.status!==201)
    {
      throw new Error('create product failed')
    
    }
    return res.json();
  }).then(resData=>{
    console.log(resData);
    const product={
      title: resData.title,
      price: resData.price,
      description: resData.description,
      imageUrl: resData.imageUrl,
    }
     this.setState(prevState=>{
      let updatedProducts= [...prevState.products];
      updatedProducts = prevState.products.concat(product);
      return {products: updatedProducts}

    }

    
    )
  
  })
  .catch(err=>{
    console.log(err)
  })
  

}

deleteHandler=(prodId)=>{
  
  console.log(prodId)
  fetch('http://localhost:8080/delete-product/' + prodId,
  {
    method: "DELETE"
  }
  ).then(res=>{
    if(res.status!==200 && res.status!==201){
      throw new Error ('Delete product failed')
    }
    return res.json()

  }).then(resData=>{
    console.log(resData)
    const deletedProduct = resData.product;
    const updatedProducts= this.state.products.filter(prod=>prod._id!==deletedProduct._id);
    this.setState({products: updatedProducts})

  }).catch(err=>{
       console.log(err)
  })
}

editHandler=(prodId)=>{
  console.log(prodId)
 const prod = this.state.products.find(p=>p._id===prodId)
  console.log(prod)
  this.setState({
    selectedProduct: prod
  })
 console.log(this.state.selectedProduct)
}

addToCartHandler=(prodId)=>{
  
    const product = {...this.state.products.find(p=>prodId===p._id)}
    const prodPrice = product.price;
    console.log(this.state)
    const newItem = [{
      product: product,
      quantity: 1
    }
     ]
 this.setState(prevState=>{
       let updatedCart = {...prevState.cart}
     let updatedCartItems= [...updatedCart.items];
    let updatedPrice = [updatedCart.totalPrice]
    updatedPrice = +updatedPrice +  +prodPrice;
     updatedCartItems =prevState.cart.items.concat(newItem)
     console.log(updatedPrice)
      return{ cart: {...this.state.cart, items: updatedCartItems, totalPrice: updatedPrice}}
     // this.setState({ someProperty: { ...this.state.someProperty, flag: false} });
    })
    }

removeFromCartHandler=(prodId, prodPrice)=>{
 
  this.setState(prevState=>{

    let updatedCart = {...prevState.cart}
    let updatedCartItems= [...updatedCart.items];
    let updatedPrice = [updatedCart.totalPrice]
    updatedPrice = +updatedPrice -  +prodPrice;
   updatedCartItems= updatedCart.items.filter(p=>prodId!==p.product._id)
    console.log( updatedCartItems)
    return{
      cart:{...this.state.cart, items: updatedCartItems, totalPrice: updatedPrice}
      
    }
 
  })
  }

  addOrderHandler=(data)=>{
    console.log(data,this.state.cart)
    fetch('http://localhost:8080/add-order/',{
      method: 'POST',
      headers: {
       'Content-Type': 'application/json',
         },
      body: JSON.stringify({
        name: data.name,
        phone: data.phone,
        email: data.email,
        address: data.address,
        order: this.state.cart
      })
     }).then(res=>{
      if( res.status==200 && res.status==201){
        throw new Error('Coul\'d not place your order')
      }

      return res.json()

     }).then(resData=>{
       console.log(resData)
       this.setState(prevState=>{
        let updatedCart = {...prevState.cart}
        let updatedCartItems= [...updatedCart.items];
       let updatedPrice = [updatedCart.totalPrice]
       updatedCartItems = [];
       updatedPrice = null;
       return{ cart: {...this.state.cart, items: updatedCartItems, totalPrice: updatedPrice}}
       })
     }).catch(err=>{
       console.log(err)
     })
}

  quantityHandler=(quan, prodId)=>{
      console.log(quan,prodId)
      this.setState(prevState=>{
      let updatedCart = {...prevState.cart}
      let updatedCartItems= [...updatedCart.items];
      let updatedPrice = [updatedCart.totalPrice]
      let updatedCartItemIndex =  updatedCart.items.findIndex(p=>prodId===p.product._id)

      updatedCartItems[updatedCartItemIndex].quantity = quan;
      updatedPrice = +updatedPrice + quan* +updatedCartItems[updatedCartItemIndex].product.price;
      
     return{ cart:{...this.state.cart, items: updatedCartItems, totalPrice : updatedPrice}}
    })
  }

  clickedEditHandler=(prodData)=>{
    console.log(prodData)
    fetch('http://localhost:8080/edit-product/' + this.state.selectedProduct._id,{
      method: 'PUT',
      headers:{
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        title: prodData.title,
        price: prodData.price,
        description: prodData.description,
        imageUrl : '',
       })
    }).then(res=>{
      if(res.status!==200 && res.status!==201){
        throw new Error('Could not edit product')
      }
      return res.json()
    }).then(resData=>{
      console.log(resData)
      this.setState({selectedProduct: null})
    })
  }

  render() 
  {

    return(
      <Auxiliary>
        <Header isAuth={this.state.isAuth}/>
        <SideBar />
        <Switch>
           <Route path='/' exact render={(props) => <Shop {...props}  addToCart={this.addToCartHandler} editHandler={this.editHandler} delete={this.deleteHandler} isAuth={this.state.isAuth} products={this.state.products}/>}/>
           <Route path="/add-product" render={(props) =><AddProductForm editMode={false} {...props}  isAuth={this.state.isAuth} clicked={this.addProductHandler} />}/>
           //<Route path="/signup" render={() =><Signup  clicked={this.signupHandler}/>}/>
           <Route path="/login" render={(props) =><Login {...props}  clicked={this.loginHandler}/>}/>
           <Route path="/product/:prodId" exact render={(props)=><ProductView {...props} />   }></Route>
          <Route path='/edit-product'  render={(props)=><AddProductForm clickedEdit ={this.clickedEditHandler} product={this.state.selectedProduct} editMode={true} {...props} isAuth={this.state.isAuth}/>}/>
          <Route path='/Cart' render={(props)=><Cart remove={this.removeFromCartHandler} quantityHandler={this.quantityHandler} cart={this.state.cart} {...props}/>}/>
          <Route path='/Checkout'  render={(props)=><Checkout  {...props} cart={this.state.cart} clicked={this.addOrderHandler}/>}/>
          <Redirect path='/'/>
     </Switch>
      </Auxiliary>
  )
  }
  

}

