import React from 'react';
import classes from './article.module.css';
import Auxiliary from '../../hoc/Auxiliary/Auxiliary'
const article=(props)=>{

    let buttons = null;
    
    if(props.isAuth){
        buttons =  (<div className={classes.Actions}>
            <a href="" className={classes.Btn}>Edit</a>
            <button  className={classes.Btn}>Details</button>
            <button  onClick={()=>props.delete(props.prodId)} className={classes.Btn}>Delete</button>
            </div>)
    }else{
        buttons=(<div className={classes.Actions}>
            <a href="" className={classes.Btn}>Add to Cart</a>
            <a href="/products/" className={classes.Btn}>Details</a>
               </div> )
    }
    
    return( 
    
    <div className={classes.Item}>

    <header className={classes.CardHeader}>
        <h1 className={classes.Title }>{ props.title }</h1>
    </header>

    <div className={classes.Image}>
        <img src={props.image} alt={props.title}/>
        
    </div>

    <div className={classes.Content}>
        <h2 className={classes.Price}>{props.price}</h2>
        <p className={classes.Description}>{props.description}</p>
    </div>
    
    <div className={classes.Actions}>
 
                    {buttons}
    </div>
</div>)
}

export default article;
