import Header from '../../components/Header/Header';
import React from 'react';
import Shop from '../../container/shop/shop';
import Auxiliary from '../Auxiliary/Auxiliary';
import SideBar from '../../components/SideBar/sideBar';
import { Route, Switch } from 'react-router';
import AddProductForm from '../../components/Add-Product From/addProductForm'
import Login from '../../components/Form/Auth/Login/login';
class Body {

addProductHandler=(event,prodData)=>{

}

inputChangedHandler = (event,identifier)=>{
  let price, title, description
    if(identifier==='title')
    {
    title= event.target.value;
    
    }
    if(identifier==='price')
    {
        price = event.target.value
       
    }
    // if(identifier==='image')
    // {
    //     image = event.target.file
       
    // }
    if(identifier==='desc')
    {
         description = event.target.value
       
    }
    
    this.setState({
        title: title,
        price: price,
        description: description

    })

}

  render(){
    return(
      <Auxiliary>
        <Header/>
        <SideBar />
        <Switch>
           <Route path='/Home' exact component={() => <Shop />}/>
           <Route path="/admin/add-product/" component={() =><AddProductForm changed onclick={this.addProductHandler} />}/>
           <Route path="/admin/login/" component={() =><Login />}/>
     </Switch>

      </Auxiliary>
  )
  }
  
}


export default layout;
