import React, { Component } from 'react';
import demoImage from '../../assets/images/book.png';
import Article  from '../../components/Article/article';
import classes from './shop.module.css';
class Shop extends Component{

    state= {
            products: [  ]
    }

    componentDidMount(){
        fetch('http://localhost:8080/')
        .then(res=>{
            console.log(res)
            return res.json();
          this.setState({
              products: res.products
          })
        }).catch(err=>{
            console.log(err)
        })
    }

    render(){
        if(!this.state.products){
            return (
                <h1>No Products Found!</h1>
            )
        }

        let products ;
        products = this.state.products.map(art=>{
           return( <Article
             title={art.title} 
             price={art.price}  
             description={art.description}
             image={art.image}
             userId={art.userId}
             key={art.title}/>)
        })
        return (
        <div className={classes.Grid}>{products}  </div> 
    )

    }
    
}

export default Shop;
