import React, { Component } from 'react';
import demoImage from '../../assets/images/book.png';
import Article  from '../Article/article';
import classes from './shop.module.css';
const shop=(props)=>{

        if(!props.products){
            return (
                <h1>No Products Found!</h1>
            )
        }

        let prods ;
        console.log(props.products)
        prods = props.products.map(art=>{
           return( <Article
             prodId={art._id}
             isAuth={props.isAuth}
             title={art.title} 
             price={art.price}  
             description={art.description}
             image={art.image}
          
             key={art._id}/>)
        })
        return (
        <div className={classes.Grid}>{prods}  </div> 
    )

    }



export default shop;
