}

addProductHandler=(prodData)=>{

fetch('http://localhost:8080/add-product/',{
       method: 'POST',
       headers: {
        'Content-Type': 'application/json',
          },
       body: JSON.stringify({
        title: prodData.title,
        price: prodData.price,
        description: prodData.description,
       imageUrl : '',
       })
  }).then(res=>{
    if(res.status!==200 && res.status!==201)
    {
      throw new Error('create product failed')
    
    }
    return res.json();
  }).then(resData=>{
    console.log(resData);
    const product={
      title: resData.title,
      price: resData.price,
      description: resData.description,
      imageUrl: resData.imageUrl,
    }
     this.setState(prevState=>{
      let updatedProducts= [...prevState.products];
      updatedProducts = prevState.products.concat(product);
      return {products: updatedProducts}

    })
  
    console.log(this.state)
  })
  .catch(err=>{
    console.log(err)
  })
  

}

deleteHandler=(prodId)=>{
  
  console.log(prodId)
  fetch('http://localhost:8080/delete-product/' + prodId,
  {
    method: "DELETE"
  }
  ).then(res=>{
    if(res.status!==200 && res.status!==201){
      throw new Error ('Delete product failed')
    }
    return res.json()

  }).then(resData=>{
    console.log(resData)
    const deletedProduct = resData.product;
    const updatedProducts= this.state.products.filter(prod=>prod._id!==deletedProduct._id);
    this.setState({products: updatedProducts})

  }).catch(err=>{
       console.log(err)
  })
}

editHandler=(prodId)=>{
  console.log(prodId)
 const prod = this.state.products.find(p=>p._id===prodId)
  console.log(prod)
  this.setState({
    selectedProduct: prod
  })
 console.log(this.state.selectedProduct)
}

addToCartHandler=(prodId)=>{
  
    const product = {...this.state.products.find(p=>prodId===p._id)}
    console.log(this.state)
   this.setState(prevState=>{
      let updatedCart = [...prevState.cart];
      updatedCart =prevState.cart.concat(product)
      console.log(updatedCart)
      return{
        cart: updatedCart
 }
    })

    
}
  render() 
  {

    return(
      <Auxiliary>
        <Header isAuth={this.state.isAuth}/>
        <SideBar />
        <Switch>
           <Route path='/Home' exact render={(props) => <Shop {...props}  addToCart={this.addToCartHandler} editHandler={this.editHandler} delete={this.deleteHandler} isAuth={this.state.isAuth} products={this.state.products}/>}/>
           <Route path="/add-product" render={(props) =><AddProductForm editMode={false} {...props}  isAuth={this.state.isAuth} clicked={this.addProductHandler} />}/>
           //<Route path="/signup" render={() =><Signup  clicked={this.signupHandler}/>}/>
           <Route path="/admin/login" render={(props) =><Login {...props}  clicked={this.loginHandler}/>}/>
           <Route path="/:prodId" render={(props)=><ProductView {...props} />   }></Route>
          <Route path='/edit-product'  render={(props)=><AddProductForm product={this.state.selectedProduct} editMode={true} {...props} isAuth={this.state.isAuth}/>}/>
     </Switch>

      </Auxiliary>
  )
  }
  

}

export default withRouter( Body);
