import React , {Component} from 'react';
import Input from '../Form/input';
import Auxiliary from '../../hoc/Auxiliary/Auxiliary';
import classes from './admin.module.css';

class Admin extends Component {

    state={  
        title: '',
        price : '',
        description : ''
    }

    

    
  inputChangedHandler = (event,identifier)=>{
      let price, title, description
        if(identifier==='title')
        {
        title= event.target.value;
        
        }
        if(identifier==='price')
        {
            price = event.target.value
           
        }
        // if(identifier==='image')
        // {
        //     image = event.target.file
           
        // }
        if(identifier==='desc')
        {
             description = event.target.value
           
        }
        
        this.setState({
            title: title,
            price: price,
            description: description

        })

 }

render(){

    return (
        <Auxiliary>
        <div >
        <Input 
        id = 'title'
        onChange={(event)=>this.inputChangedHandler(event,'title')} 
        elementtype='text' 
        label='Title'/>

<Input 
        id= "price"
        onChange={(event)=>this.inputChangedHandler(event,'price')} 
        elementtype='text' 
        label='Price'/>
    
    <Input 
        id="image"
        onChange={(event)=>this.inputChangedHandler(event,'file')} 
        elementtype='file' 
        label='Image'/>
    
<Input
id="description"
onChange={(event)=>this.inputChangedHandler(event,'desc')} 
elementtype="textArea"
label="Desciption"
/>

        </div>

<div >
<a href="" className={classes.Btn}>Details</a>

</div>
</Auxiliary>
    )
    


} 
}


export default Admin;
