    render(){
        if(!this.state.products){
            return (
                <h1>No Products Found!</h1>
            )
        }

        let products ;
        products = props.products.map(art=>{
           return( <Article
             title={art.title} 
             price={art.price}  
             description={art.description}
             image={art.image}
             userId={art.userId}
             key={art.title}/>)
        })
        return (
        <div className={classes.Grid}>{products}  </div> 
    )

    }
    
}

export default Shop;
