import Header from '../../components/Header/Header';
import React, {Component} from 'react';
import Shop from '../../container/shop/shop';
import Auxiliary from '../Auxiliary/Auxiliary';
import SideBar from '../../components/SideBar/sideBar';
import { Route, Switch, Redirect, withRouter } from 'react-router-dom';
import AddProductForm from '../../components/Add-Product From/addProductForm'
import Signup from '../../components/Form/Auth/signup/signup';
import Login from '../../components/Form/Auth/login/login';
class Body extends Component {

  state={  
   
    products: []
}
componentDidMount(){
  fetch('http://localhost:8080/')
  .then(res=>{
      
      return res.json();
   
  }).then(resData=>{
      console.log(resData)
      this.setState({
          products: resData.products
      })
  })
  .catch(err=>{
      console.log(err)
  })
}


signupHandler=(adminData)=>{
  fetch('http://localhost:8080/signup/',{
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body:JSON.stringify({
body:{
      email: adminData.email,
      password: adminData.password,
      name: adminData.name
}
    })
  }).then(res=>{
    if(res.status!==200 && res.status!==201)
    {
      throw new Error('create admin failed')
    
    }
    return res.json();
}).then(resData=>{
  console.log(resData)
}).catch(err=>{
  console.log(err)
})


addProductHandler=(prodData)=>{

fetch('http://localhost:8080/add-product/',{
       method: 'POST',
       headers: {
        'Content-Type': 'application/json',
          },
       body: JSON.stringify({
        title: prodData.title,
        price: prodData.price,
        description: prodData.description,
       imageUrl : '',
       })
  }).then(res=>{
    if(res.status!==200 && res.status!==201)
    {
      throw new Error('create product failed')
    
    }
    return res.json();
  }).then(resData=>{
    console.log(resData);
    const product={
      title: resData.title,
      price: resData.price,
      description: resData.description,
      imageUrl: resData.imageUrl,
    }
     this.setState(prevState=>{
      let updatedProducts= [...prevState.products];
      updatedProducts = prevState.products.concat(product);
      return {products: updatedProducts}

    })
    this.history.replace('/')
    console.log(this.state)
  })
  .catch(err=>{
    console.log(err)
  })
  

}


  render() 
  {

    return(
      <Auxiliary>
        <Header/>
        <SideBar />
        <Switch>
           <Route path='/Home' exact component={() => <Shop products={this.state.products}/>}/>
           <Route path="/admin/add-product/" component={() =><AddProductForm  clicked={this.addProductHandler} />}/>
           <Route path="/admin/signup/" component={() =><Signup clicked={this.signupHandler}/>}/>
           <Route path="/admin/login/" component={() =><Login />}/>
     </Switch>

      </Auxiliary>
  )
  }
  
}
}

export default withRouter( Body);
