import React , {Component, useEffect} from 'react';
import Input from '../Form/input';
import Auxiliary from '../../hoc/Auxiliary/Auxiliary';
import classes from './admin.module.css';

const addProductForm= (props)=>{


    let product = null ;
 
    let price, title, description,elements,image
if(props.editMode){
    product = props.product
    price = product.price; title=product.title; description= product.description; 
}
 

    const inputChangedHandler = (event,identifier)=>{
        if(identifier==='title')
        {
        title= event.target.value;
        
        }
        if(identifier==='price')
        {
            price = event.target.value
           
        }
        if(identifier==='image')
        {
            console.log(event.target.files)
            image=event.target.files[0]
          
        }
        if(identifier==='desc')
        {
             description = event.target.value
           
        }
        
     
    }
const clickedHandler=(pri,ti,desc,im)=>{
   
  const productData={
      price: pri,
      title: ti,
      description: desc,
      image: im
  }
  console.log(productData)
       props.clicked(productData);
       props.history.replace('/')
}
const EditClickHandler=(pri,ti,desc,im)=>{
      if(!im){
          im ={ path: props.product.imageUrl} 
      }
    const productData={
        price: pri,
        title: ti,
        description: desc,
        image: im
    }
         props.clickedEdit(productData);
         props.history.replace('/')
  }


        
    if(props.editMode){
      elements = ( <div className={classes.Form}>
        <Input 
        id = 'title'
        onChange={(event)=>inputChangedHandler(event,'title')} 
        defaultValue={product.title}
        elementType='text' 
        label='Title'/>

       <Input 
        id= "price"
        onChange={(event)=>inputChangedHandler(event,'price')} 
        elementType='text' 
        defaultValue={product.price}
        label='Price'/>
    
    <Input 
        id="image"
        onChange={(event)=>inputChangedHandler(event,'image')} 
        elementType='file' 
        accept='png, .jpg,.jpeg'
        label='Image'/>
    
<Input
id="description"
onChange={(event)=>inputChangedHandler(event,'desc')} 
defaultValue={product.description}
elementType="textArea"
label="Desciption"
/>
<div >
<button  className={classes.Btn} onClick={()=>EditClickHandler(price,title,description,image)}>Okay</button>
<button  className={classes.Btn}>Cancel</button>

</div>

        </div>
        
      )
    } 
    else{
        elements =   (  <Auxiliary>
        <div className={classes.Form}>
        <Input 
        id = 'title'
        onChange={(event)=>inputChangedHandler(event,'title')} 
        elementType='text' 
        label='Title'/>

        <div className={classes.Input}>
            <label 
         className={classes.Label}>
         Category
        </label>
        <select
        id = 'category'
        classesName={classes.InputElement}
        onChange={(event)=>inputChangedHandler(event,'category')} 
        type='select' 
        >
          <option>
                Desi
          </option>
        </select>
        </div>

<Input 
        id= "price"
        onChange={(event)=>inputChangedHandler(event,'price')} 
        elementType='text' 
        label='Price'/>
    
    <Input 
        id="image"
        onChange={(event)=>inputChangedHandler(event,'image')} 
        elementType='file' 
        accept='png, .jpg,.jpeg'
        label='Image'/>
    
<Input
id="description"
onChange={(event)=>inputChangedHandler(event,'desc')} 
elementType="textArea"
label="Desciption"
/>

        </div>

<div >
<button  className={classes.Btn} onClick={()=>clickedHandler(price,title,description,image)}>Add</button>
<button  className={classes.Btn}>Cancel</button>

</div>
</Auxiliary>)
    }

    return (elements )
    



}


export default addProductForm;
