import React , {Component, useEffect} from 'react';
import Input from '../Form/input';
import Auxiliary from '../../hoc/Auxiliary/Auxiliary';
import classes from './admin.module.css';

const addProductForm= (props)=>{
    let product = null ;
    console.log(props.product)
    let price, title, description,elements
if(props.editMode){
 
    price = product.price; title=product.title; description= product.description;
}
 

    const inputChangedHandler = (event,identifier)=>{
        if(identifier==='title')
        {
        title= event.target.value;
        
        }
        if(identifier==='price')
        {
            price = event.target.value
           
        }
        // if(identifier==='image')
        // {
        //     image = event.target.file
           
        // }
        if(identifier==='desc')
        {
             description = event.target.value
           
        }
        
     console.log(price,title,description)
    }
const clickedHandler=(pri,ti,desc)=>{
  const productData={
      price: pri,
      title: ti,
      description: desc
  }
       props.clicked(productData);
}
const EditClickHandler=(pri,ti,desc)=>{
    const productData={
        price: pri,
        title: ti,
        description: desc
    }
         props.clickedEdit(productData);
  }


        
    if(props.editMode){
      elements = ( <div className={classes.Form}>
        <Input 
        id = 'title'
        onChange={(event)=>inputChangedHandler(event,'title')} 
        defaultValue={product.title}
        elementType='text' 
        label='Title'/>

       <Input 
        id= "price"
        onChange={(event)=>inputChangedHandler(event,'price')} 
        elementType='text' 
        defaultValue={product.price}
        label='Price'/>
    
    <Input 
        id="image"
        onChange={(event)=>inputChangedHandler(event,'file')} 
        elementType='file' 
        label='Image'/>
    
<Input
id="description"
onChange={(event)=>inputChangedHandler(event,'desc')} 
defaultValue={product.description}
elementType="textArea"
label="Desciption"
/>
<div >
<button  className={classes.Btn} onClick={()=>EditClickHandler(price,title,description)}>Okay</button>
<button  className={classes.Btn}>Cancel</button>

</div>

        </div>
        
      )
    } 
    else{
        elements =   (  <Auxiliary>
        <div className={classes.Form}>
        <Input 
        id = 'title'
        onChange={(event)=>inputChangedHandler(event,'title')} 
        elementType='text' 
        label='Title'/>

<Input 
        id= "price"
        onChange={(event)=>inputChangedHandler(event,'price')} 
        elementType='text' 
        label='Price'/>
    
    <Input 
        id="image"
        onChange={(event)=>inputChangedHandler(event,'file')} 
        elementType='file' 
        label='Image'/>
    
<Input
id="description"
onChange={(event)=>inputChangedHandler(event,'desc')} 
elementType="textArea"
label="Desciption"
/>

        </div>

<div >
<button  className={classes.Btn} onClick={()=>clickedHandler(price,title,description)}>Add</button>
<button  className={classes.Btn}>Cancel</button>

</div>
</Auxiliary>)
    }

    return (elements )
    



}


export default addProductForm;
