import React from 'react';
import { Component } from 'react';


class Layout extends Component{
 state={
    showSideBar: true,
 }
 render(){
    return(
        <div>
        <Header clicked={this.state.toggleSideBarHandler} logout={this.props.logout} isAuth={this.props.isAuth}/>
        <SideBar />
        <SideDrawer show={this.state.showSideBar}/>
        </div>
    )
 }
}

export default Layout;
