import Header from '../../components/Header/Header';
import React, {Component} from 'react';
import Shop from '../../components/shop/shop';
import Auxiliary from '../../hoc/Auxiliary/Auxiliary';
import SideBar from '../../components/SideBar/sideBar';
import { Route, Switch, Redirect, withRouter } from 'react-router-dom';
import AddProductForm from '../../components/Add-Product From/addProductForm'
import Signup from '../../components/Auth/signup/signup';
import Login from '../../components/Auth/login/login';
import ProductView from '../../components/Article/Product-View/product-view';
import Cart from '../../components/cart/cart';
import Checkout from '../../components/checkout/checkout';

class Body extends Component {

  state={  
    isAuth : false,
    products: null,
    cart: {
      items:[],
     totalPrice: null
    }
}
componentDidMount(){
  fetch('http://localhost:8080/get-products')
  .then(res=>{
      
      return res.json();
   
  }).then(resData=>{
      console.log(resData)
      this.setState({
          products: resData.products
      })
  })
  .catch(err=>{
      console.log(err)
  })
}


signupHandler=(adminData)=>{
  console.log(adminData)
  fetch('http://localhost:8080/signup/',{
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({

      email: adminData.email,
      password: adminData.password,
      name: adminData.name

    })
  }).then(res=>{
    if(res.status!==200 && res.status!==201)
    {
      throw new Error('create admin failed')
    
    }
    return res.json();
}).then(resData=>{
  console.log(resData)
  
}).catch(err=>{
  console.log(err)
})
this.props.history.replace('/home')
}

loginHandler=(loginData)=>{

  console.log(loginData)
  this.setState({
    isAuth: true
  })
 
