import React, { Component } from 'react';

import Article  from '../../Article/article';
import classes from './shop.module.css';
const shop=(props)=>{

    if(props.loading){
             return (
                <h1>loading !</h1>
            )
        }

        if(!props.products || props.products.length===0){
            return (
                <h1>No Products !</h1>
            )
        }
    
        console.log(this.prop.params)

        let prods ;
        
        prods = props.products.map(art=>{
            console.log(art._id)
           return( <Article
             addToCart={props.addToCart}
             key={art._id}
             editHandler={props.editHandler}
             id={art._id}
             prodId={art._id}
             isAuth={props.isAuth}
             title={art.title} 
             price={art.price}  
             description={art.description}
             imageUrl={ art.imageUrl}
             delete={props.delete}
             key={art._id}/>)
        })
        return (
        <div className={classes.Grid}>{prods}  </div> 
    )

    }



export default shop;
