//                                 <% order.products.forEach(p => { %>
//                                     <li class="orders__products-item"><%= p.product.title %> (<%= p.quantity %>)</li>
//                                 <% }); %>
//                             </ul>
//                         </li>
//                     <% }); %>
//                 </ul>

  
    render(){
        let orders;
        if(!this.state.orders)
        {
           return orders = <h1>No orders</h1>
        }
      
        orders = this.state.orders.map(order=>{
            console.log( order.orderItems.items)
            const items = order.orderItems.items.map(item=>{
                
                return(<li>
                {"Product: " + item.product.title + " " + item.quantity}
                </li>)
                
                  
            })
            return( 
                <li className='orders__item'>
                    <h1>
                        Order - {order._id}
                       
                    </h1>
                    <h1> Name: {order.name}</h1>
                    <h1> Adress: {order.address}</h1>
                    <h1>Time:{}</h1>
                    <ul className="orders__products-item">
                    
                            {items}
                            <h1></h1>
                    </ul>
                </li>)
            })

            
        return ( 
        <div className="Orders">
        <h1>Orders</h1> 
        <div className="column-labels">
        <label className="product-image">Image</label>
        <label className="product-details">Product</label>
        <label className="product-price">Price</label>
        <label className="product-quantity">Quantity</label>
        <label className="product-removal">Remove</label>
        <label className="product-line-price">Total</label>
      </div>
          <ul className="orders">
            
            {orders}
             </ul>
             <div className="totals">
    <div className="totals-item">
      <label>Subtotal</label>
      <div className="totals-value" id="cart-subtotal">{subTotal? subTotal : 0}
      </div>
    </div>
    </div>
      </div>
      )
        
    }

}

export default Order
