import React, {Component} from 'react';
import './productView.css'
class ProductView extends Component{

componentDidMount(){

    const prodId =  this.props.match.params.prodId;
    viewProductHandler=(prodId)=>{
        console.log(prodId)
        fetch('http://localhost:8080/get-product/' + prodId,
        { method: "GET",
          headers:{
            'Content-Type' : 'application/json'
          }
          }).then(res=>{
            if(res.status!==200 && res.status!==201){
              throw new Error(' could not fetch product')
            }
            
            return res.json()
          }).then(resData=>{
            console.log(resData)
            const product= resData.product
            this.setState=({
              selectedProduct:product
            })
          }).catch(err=>{
            console.log(err)
          })
      
      }

}

     
   
    render(){
        product = this.props.product;
return (<main className='centered'>
            <h1>{product.title}</h1>
            <div >
                <img class="image" src={product.imageUrl} alt={product.title} />
            </div>
            <h2>{product.price}</h2>
            <p>{product.description}</p>
            
            </main> )

    }
    
    
}

export default ProductView;
