import React from 'react';
import Auth from '../auth';
import Input from '../../input';
import './signup.css'

const signup=(props)=>{

    const inputChangedHandler = (event,identifier)=>{
        if(identifier==='title')
        {
        title= event.target.value;
        
        }
        if(identifier==='price')
        {
            price = event.target.value
           
        }
        // if(identifier==='image')
        // {
        //     image = event.target.file
           
        // }
        if(identifier==='desc')
        {
             description = event.target.value
           
        }
        
     console.log(price,title,description)
    }

const clickedHandler=(pri,ti,desc)=>{
  const productData={
      price: pri,
      title: ti,
      description: desc
  }
       props.clicked(productData);
}



return( 
<Auth>
        
        <form>
        <h2>Signup</h2>
        <Input 
        id = 'name'
        onChange={(event)=>inputChangedHandler(event,'title')} 
        elementtype='text' 
        label='Name'/>

    <Input 
        id = 'email'
        onChange={(event)=>inputChangedHandler(event,'title')} 
        elementtype='email' 
        label='Email'/>
 
    <Input 
        id = 'password'
        onChange={(event)=>inputChangedHandler(event,'title')} 
        elementtype='password' 
        label='Password'/>
        
        <button className="Btn">Signup</button>
        </form>
    </Auth>
)

}


export default signup
